using UnityEngine;using System.Collections;/** * Pencil in Arteachery */public class ArteacheryPencil : MonoBehaviour{	GameManager.ePlayers player;	ArteacheryLevelManager alvm;	void Start ()	{		alvm = GameObject.Find ("SceneManager").GetComponent<ArteacheryLevelManager> () as ArteacheryLevelManager;	}		public void setPlayer (GameManager.ePlayers player)	{		this.player = player;		gameObject.GetComponent<SpriteRenderer> ().sprite = Resources.Load <Sprite> ("Sprites/ArcheryLevel/pen/" + GameManager.Instance.GetColor (player));	}		public GameManager.ePlayers getPlayer ()	{		return player;	}		void OnTriggerEnter2D (Collider2D other)	{		if (other.tag == "Target") {			other.GetComponent<ArteacheryTeacher> ().Hitted ();			Destroy (gameObject);			alvm.Score (player);		}		if (other.tag == "Bound") {			Destroy (gameObject);		}		if (other.tag == "Bullet") {			Destroy (gameObject);			Destroy (other.gameObject);		}	}}