using UnityEngine;using System.Collections;/** * Bus in DelayRace */public class DelayraceBus : MonoBehaviour{	LevelManager lvm;	int init_speed = 3000;	int speed = 8000;	float turbo = 1.3f;	void Awake ()	{		lvm = GameObject.Find ("LevelManager").GetComponent<LevelManager> ();		lvm.OnCountdown += InitBus;		lvm.OnStart += MoveBus;		lvm.OnFinish += RemoveCollider;	}	void InitBus ()	{		rigidbody2D.AddForce (Vector3.right * init_speed);	}	void MoveBus ()	{		rigidbody2D.AddForce (Vector3.right * speed);	}	void RemoveCollider ()	{		GetComponent<BoxCollider2D> ().enabled = false;	}	private void OnTriggerEnter2D (Collider2D other)	{		if (other.gameObject.tag == "Player")			rigidbody2D.AddForce (Vector3.right * speed * turbo);		if (other.gameObject.tag == "Target") {			Vector3 final_position = transform.position;			final_position.x = other.transform.position.x + 420f;			transform.position = final_position;			collider2D.isTrigger = false;			RemoveCollider ();			rigidbody2D.velocity = Vector2.zero;		}	}}