using UnityEngine;using UnityEngine.UI;using System;using TouchScript.Gestures;/** * Select number page behavior */public class SelectNumber : GenericMenu{		void Start ()	{		if (GameManager.Instance.GetGameMode () == GameManager.eGameMode.CLASSIC) {			GameObject btn_1p = GameObject.Find ("btn_1p");			btn_1p.GetComponent<Button> ().interactable = false;		}	}	/**	 * Set number of players	 */	public void StartWithPlayers (int num_players)	{		GameManager.Instance.CreatePlayers (num_players);		MenuManager.SelectPlayer ();	}}