using UnityEngine;using System.Collections.Generic;using System.Linq;/** * Scene loader */public static class MenuManager{	public static void LevelOver ()	{		GameManager.Instance.Level = ((GameManager.eLevels)(GameManager.Instance.Level.GetHashCode () + 1));		if (GameManager.Instance.GetGameMode () == GameManager.eGameMode.CLASSIC)			Summary ();		else			SelectLevel ();	}	public static void NewGame ()	{		Application.LoadLevel ("Home");	}	public static void StartHome ()	{		Application.LoadLevel ("Home");	}		public static void SelectNumber ()	{		Application.LoadLevel ("SelectNumber");	}	public static void SelectMode ()	{		Application.LoadLevel ("SelectMode");	}	public static void SelectLevel ()	{		Application.LoadLevel ("SelectLevel");	}		public static void SelectPlayer ()	{		Application.LoadLevel ("SelectPlayer");	}	public static void Credits ()	{		Application.LoadLevel ("Credits");	}		public static void Summary ()	{		Application.LoadLevel ("Summary");	}		public static void Award ()	{		Application.LoadLevel ("Award");	}		public static void StartGame ()	{		GameManager.Instance.Level = (GameManager.eLevels)(GameManager.Instance.Level.GetHashCode () + 1);		Application.LoadLevel (GameManager.Instance.Level.ToString ());	}	public static void NextLevel ()	{		Application.LoadLevel (GameManager.Instance.Level.ToString ());	}		public static void StartLevel (GameManager.eLevels level)	{		GameManager.Instance.Level = level;		Application.LoadLevel (((GameManager.eLevels)level.GetHashCode ()).ToString ());	}}