using UnityEngine;using System.Collections;/** * Play menu music through multiple scenes */public class MenuMusicManager : MonoBehaviour{	void Awake ()	{		if (MusicManager.Instance.Source.clip != MusicManager.Instance.MenuClip) {			MusicManager.Instance.Source.Stop ();			MusicManager.Instance.Source.clip = MusicManager.Instance.MenuClip;			MusicManager.Instance.Source.Play ();		}	}}